fn wots_generic_signature_verify<S>(public_key: &str, message: &str, signature: &str, depth: u8, index: u64)
where
    S: Sponge + Default,
{
    let public_key_trits = TryteBuf::try_from_str(public_key)
        .unwrap()
        .as_trits()
        .encode::<T1B1Buf>();
    let message_trits = TryteBuf::try_from_str(message).unwrap().as_trits().encode::<T1B1Buf>();
    let signature_trits = TryteBuf::try_from_str(signature)
        .unwrap()
        .as_trits()
        .encode::<T1B1Buf>();

    let public_key = MssPublicKey::<S, WotsPublicKey<S>>::from_trits(public_key_trits)
        .unwrap()
        .depth(depth);
    let signature = MssSignature::<S>::from_trits(signature_trits).unwrap().index(index);
    let valid = public_key.verify(&message_trits, &signature).unwrap();

    assert!(valid);
}

